import React, { useEffect, useRef } from "react";
import "./App.css";

const App = () => {
  const vehicleRef = useRef(null);
  const reverseVehicleRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          // Trigger the first vehicle's animation
          vehicleRef.current.classList.add("animate");

          // Wait for the first animation to complete, then trigger the second
          vehicleRef.current.addEventListener("animationend", () => {
            reverseVehicleRef.current.classList.add("animate");
          });

          // Disconnect observer after starting the first animation
          observer.disconnect();
        }
      },
      { threshold: 0.5 }
    );

    if (vehicleRef.current) {
      observer.observe(vehicleRef.current);
    }

    return () => {
      if (observer) observer.disconnect();
    };
  }, []);

  return (
    <div className="road">
      {/* First Vehicle */}
      <div className="vehicle" ref={vehicleRef}></div>
      {/* Second Vehicle */}
      <div className="vehicle-reverse" ref={reverseVehicleRef}></div>
    </div>
  );
};

export default App;
