import React, { useState, useEffect } from 'react';
import { Motion } from 'lucide-react';

const MovingObjectLine = () => {
  const [currentPoint, setCurrentPoint] = useState(0);
  const [isMoving, setIsMoving] = useState(true);
  const totalPoints = 5;
  const pointPositions = Array.from({ length: totalPoints }, (_, i) => i);

  // Calculate positions for responsive layout
  const calculatePosition = (index) => {
    const progress = index / (totalPoints - 1);
    return {
      left: `${10 + progress * 60}%`,  // Start from 10% to 70% width
      top: `${20 + progress * 30}%`    // Start from 20% to 50% height
    };
  };

  useEffect(() => {
    if (!isMoving) return;

    const interval = setInterval(() => {
      setCurrentPoint((prev) => {
        if (prev >= totalPoints - 1) {
          setIsMoving(false);
          return prev;
        }
        return prev + 1;
      });
    }, 2000);

    return () => clearInterval(interval);
  }, [isMoving]);

  return (
    <div className="relative w-full h-screen bg-gray-100">
      {/* Line */}
      <div 
        className="absolute w-4/5 h-1 bg-gray-300 transform -rotate-15"
        style={{
          top: '35%',
          left: '10%',
          transformOrigin: 'left center'
        }}
      />

      {/* Points and Labels */}
      {pointPositions.map((point) => {
        const position = calculatePosition(point);
        return (
          <div 
            key={point}
            className="absolute"
            style={{
              ...position,
              transform: 'translate(-50%, -50%)'
            }}
          >
            {/* Point marker */}
            <div className="w-4 h-4 bg-blue-500 rounded-full" />

            {/* Point label */}
            {currentPoint >= point && (
              <div className="absolute mt-4 -ml-16 w-32 text-center font-bold text-blue-600">
                REACHED POINT {point + 1}
              </div>
            )}
          </div>
        );
      })}

      {/* Moving Object */}
      <div
        className="absolute transition-all duration-1000 ease-linear"
        style={{
          ...calculatePosition(currentPoint),
          transform: 'translate(-50%, -50%)'
        }}
      >
        <Motion 
          className="text-red-500" 
          size={32}
        />
      </div>

      {/* Reset Button */}
      {!isMoving && (
        <button
          onClick={() => {
            setCurrentPoint(0);
            setIsMoving(true);
          }}
          className="absolute bottom-8 left-1/2 transform -translate-x-1/2 
                     px-4 py-2 bg-blue-500 text-white rounded-md 
                     hover:bg-blue-600 transition-colors"
        >
          Reset Animation
        </button>
      )}
    </div>
  );
};

export default MovingObjectLine;
