// App.js
import React, { useState, useEffect } from 'react';
import { FaInfoCircle } from 'react-icons/fa';
import './App2.css';

const App = () => {
  const totalPoints = 7;
  const [containerHeight, setContainerHeight] = useState(window.innerHeight);
  const [containerWidth, setContainerWidth] = useState(window.innerWidth);
  const xSpacing = containerWidth / (totalPoints - 1);
  const ySpacing = containerHeight * 0.9 / (totalPoints - 1); // Adjust to end near the middle of the right edge
  const speed = 5; // px per frame

  const [currentPosition, setCurrentPosition] = useState({ x: 0, y: 0 });
  const [currentPointIndex, setCurrentPointIndex] = useState(0);
  const [infoPoints, setInfoPoints] = useState(Array(totalPoints).fill(false));

  useEffect(() => {
    const handleResize = () => {
      setContainerHeight(window.innerHeight);
      setContainerWidth(window.innerWidth);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    const intervalId = setInterval(() => {
      if (currentPointIndex < totalPoints) {
        const targetPosition = {
          x: currentPointIndex * xSpacing,
          y: currentPointIndex * ySpacing,
        };

        // Move the object
        setCurrentPosition((prevPosition) => {
          const nextX = prevPosition.x + (speed * Math.sign(targetPosition.x - prevPosition.x));
          const nextY = prevPosition.y + (speed * Math.sign(targetPosition.y - prevPosition.y));

          const atTargetX = Math.abs(targetPosition.x - nextX) < speed;
          const atTargetY = Math.abs(targetPosition.y - nextY) < speed;

          if (atTargetX && atTargetY) {
            setInfoPoints((prev) => {
              const newInfoPoints = [...prev];
              newInfoPoints[currentPointIndex] = true;
              return newInfoPoints;
            });
            setCurrentPointIndex((prevIndex) => prevIndex + 1);
            return targetPosition;
          }

          return {
            x: atTargetX ? targetPosition.x : nextX,
            y: atTargetY ? targetPosition.y : nextY,
          };
        });
      }
    }, 20);

    return () => clearInterval(intervalId);
  }, [currentPointIndex, xSpacing, ySpacing, totalPoints, speed]);

  return (
    <div className="line-container">
      <div
        className="moving-object"
        style={{
          left: `${currentPosition.x}px`,
          top: `${currentPosition.y}px`,
        }}
      ></div>
      {Array.from({ length: totalPoints }, (_, index) => (
        <div
          key={index}
          className="point"
          style={{
            left: `${index * xSpacing}px`,
            top: `${index * ySpacing}px`,
          }}
        >
          <FaInfoCircle className="icon" />
          {infoPoints[index] && (
            <div className="info-box">Reached Point {index + 1}</div>
          )}
        </div>
      ))}
    </div>
  );
};

export default App;

