import React, { useState, useEffect } from 'react';
import { Camera } from 'lucide-react';

const MovingObject = () => {
  const [currentPoint, setCurrentPoint] = useState(0);
  const [isMoving, setIsMoving] = useState(false);
  const totalPoints = 5;

  // Calculate positions for each point along the line
  const getPointPosition = (index) => {
    const progress = index / (totalPoints - 1);
    return {
      left: `${progress * 100}%`,
      top: progress < 0.5 
        ? `${progress * 100}%` 
        : `${(1 - progress) * 100}%`
    };
  };

  // Start/Reset animation
  const startAnimation = () => {
    setCurrentPoint(0);
    setIsMoving(true);
  };

  // Move to next point
  useEffect(() => {
    if (isMoving && currentPoint < totalPoints) {
      const timer = setTimeout(() => {
        if (currentPoint < totalPoints - 1) {
          setCurrentPoint(prev => prev + 1);
        } else {
          setIsMoving(false);
        }
      }, 2000);
      return () => clearTimeout(timer);
    }
  }, [currentPoint, isMoving]);

  return (
    <div className="w-full h-screen/2 relative bg-gray-100 overflow-hidden">
      {/* Line */}
      <div 
        className="absolute w-full h-0.5 bg-gray-300 transform origin-top-left"
        style={{
          width: '100%',
          transform: 'rotate(15deg)',
          top: '10%',
        }}
      />

      {/* Points and Labels */}
      {Array.from({ length: totalPoints }).map((_, index) => {
        const position = getPointPosition(index);
        return (
          <div
            key={index}
            className="absolute"
            style={{
              left: position.left,
              top: '10%',
              transform: 'translate(-50%, -50%)'
            }}
          >
            {/* Point marker */}
            <div className="w-4 h-4 bg-blue-500 rounded-full" />

            {/* Point label */}
            {currentPoint >= index && (
              <div 
                className="absolute mt-6 -ml-16 w-32 text-center text-sm font-bold bg-white p-2 rounded shadow-md transform -translate-x-1/2"
              >
                REACHED POINT {index + 1}
              </div>
            )}
          </div>
        );
      })}

      {/* Moving object */}
      <div
        className="absolute transition-all duration-1000 ease-linear"
        style={{
          ...getPointPosition(currentPoint),
          top: '10%',
          transform: 'translate(-50%, -50%)'
        }}
      >
        {/* <Camera className="text-red-500 w-8 h-8 animate-bounce" /> */}
      </div>

      {/* Control button */}
      <button
        onClick={startAnimation}
        className="absolute bottom-4 left-1/2 transform -translate-x-1/2 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors"
        disabled={isMoving}
      >
        {isMoving ? 'Moving...' : 'Start Animation'}
      </button>
    </div>
  );
};

export default MovingObject;
